mapper.delete:
delete
from plan
where id in (#{ids});

mapper.select:
select id,p_name,m_id,p_startTime,p_endTime,h_id,price,status,
(select m_name from movie where id = #{mId}) as m_name,
(select h_name from hall where id in (select h_id from plan where m_id = #{mId})) as h_name
from plan
where m_id = #{mId}

    and p_startTime <=#{startDate}


    and status =#{status}


    and p_endTime>=#{endDate}


    and p_name like concat('%',#{pName},'%')

select m_length
from movie
where id = #{mId}

select id, h_name
from hall
where h_name = #{name};

select id
from movie
where m_name = #{name};

select id
from plan
where p_name = #{name};

select id, p_name, p_startTime, p_endTime, status
from plan
where h_id in (select id from hall where h_name = #{hName});
