mapper.delete:
delete
from plan
where id in (#{ids});

mapper.select:
select id,p_name,m_id,p_startTime,p_endTime,h_id,price,status,
(select m_name
    FROM movie
    WHERE id = '3') as m_name,
(select h_name
    FROM hall
    WHERE id = h_id) as h_name
FROM plan
where m_id = #{mId}

    and p_startTime <=#{startDate}


    and status =#{status}


    and p_endTime>=#{endDate}


    and p_name like concat('%',#{pName},'%')

select m_length
from movie
where id = #{mId}

select id, h_name
from hall
where h_name = #{name};

select id
from movie
where m_name = #{name};

select id
from plan
where p_name = #{name};

select id, p_name, p_startTime, p_endTime, status
from plan
where h_id in (select id from hall where h_name = #{hName});

select count(*)
from plan
where h_id in #{hId}

select p_startTime,price,(select h_name from hall where h_id = hall.id) as h_name,id
from plan

    plan.status = '已上线'

        and m_id = #{mId}


        and p_endTime <= #{nextTime}


        and p_startTime >= #{time}
